import React, {useState} from "react";

const Input = ({ correctValue }) => {
    const [valueInput, setValueInput] = useState("")

    const changeValue = e => {
        setValueInput(e.target.value)
    }

    return (
        <input
            className={
                valueInput === correctValue ?
                    "correctInput" :
                    "incorrectInput"
            }
            onChange={changeValue}
        />
    )
}

export default Input
