class Column {
    constructor(title, fields) {
        this.title = title
        this.fields = fields
    }
}
class Title {
    constructor(code, number) {
        this.code = code
        this.number = number
    }
}

const sequenceNumberResponse = (start, end, inc=1, isDecreasing=false) => {
    let output = []
    if (!isDecreasing) {
        for (let i = start; i <= end; i += inc) {
            output.push({ response: i })
        }
        return output
    }
    for (let i = start; i >= end; i -= inc) {
        output.push({ response: i })
    }
    return output
}

const sequenceBooleanResponse = n => {
    let output = [{ response: false }]
    for (let i = 0; i < n - 1; i++) {
        output.unshift({ response: true })
    }
    return output
}

export const exerciseOne = [
    new Column("i", sequenceNumberResponse(0, 5)),
    new Column("console", sequenceNumberResponse(0, 5)),
    new Column("i < 5", sequenceBooleanResponse(6)),
]
export const exerciseOneTitle = new Title (
    "for (let i = 0; i < 5; i++) {\n" +
    "      console.log(i)\n" +
    "}", 1
)

